mapper.update:
update board
set likenum = likenum+1
where Idboard = #{Idboard};

update board
set likenum = likenum-1
where Idboard =#{Idboard};

mapper.select:
select MAX(currentpage) as maxpage
from(
select b.*,FLOOR(@rownum:=@rownum+1/10)+1 as currentpage
from board b
where (@rownum:=0)=0 and b.boardsubject = #{subjectname}
order by datetime desc
)as a
